mapper.select:
SELECT name
FROM member
WHERE providerUserId = #{value}

mapper.update:
  		UPDATE member
  		SET
			name = #{name}
  		where providerUserId = #{providerUserId};

  		UPDATE member
  		SET
			pointLike = pointLike + 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			pointLike = pointLike - 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			pointDislike = pointDislike + 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			pointDislike = pointDislike - 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			pointShare = pointShare + 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			pointShare = pointShare - 1 
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			lastLoginDate = NOW()
  		where providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);
