mapper.select:
SELECT name
FROM member
WHERE providerUserId = #{value}

mapper.update:
  		UPDATE member
  		SET
			`like` = `like` + 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`like` = `like` - 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`dislike` = `dislike` + 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`dislike` = `dislike` - 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`sharing` = `sharing` + 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`sharing` = `sharing` - 1 
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);

  		UPDATE member
  		SET
			`lastLoginDate` = NOW()
  		WHERE providerUserId = (
	  		SELECT providerUserId FROM UserConnection WHERE userId = #{userId}
  		);
